			break;

		case S1: /* e.g. "\\x00" */
			lx_like_ungetc(lx, c); lx_like_dynpop(lx->buf_opaque); return TOK_CHAR;

		case S2: /* e.g. "%" */
			lx_like_ungetc(lx, c); lx_like_dynpop(lx->buf_opaque); return TOK_MANY;

		case S3: /* e.g. "_" */
