const char *
lx_like_name(enum lx_like_token t)
{
	switch (t) {
	case TOK_CHAR: return "CHAR";
	case TOK_MANY: return "MANY";
	case TOK_ANY: return "ANY";
	case TOK_EOF:     return "EOF";
	case TOK_ERROR:   return "ERROR";
	case TOK_UNKNOWN: return "UNKNOWN";
	default: return "?";
	}
}

const char *
lx_like_example(enum lx_like_token (*z)(struct lx_like_lx *), enum lx_like_token t)
{
	assert(z != NULL);

	if (z == z0) {
		switch (t) {
		case TOK_CHAR: return "";
		case TOK_MANY: return "";
		case TOK_ANY: return "";
		default: goto error;
		}
	}

error:

	errno = EINVAL;
	return NULL;
}

void
lx_like_init(struct lx_like_lx *lx)
{
	static const struct lx_like_lx lx_default;

	assert(lx != NULL);

	*lx = lx_default;

	lx->c = EOF;
	lx->z = z0;

	lx->end.byte = 0;
	lx->end.line = 1;
	lx->end.col  = 1;
	(void)lx_like_dynpop;
}

enum lx_like_token
lx_like_next(struct lx_like_lx *lx)
{
	enum lx_like_token t;

	assert(lx != NULL);
	assert(lx->z != NULL);

	if (lx->lgetc == NULL) {
		return TOK_EOF;
	}

	t = lx->z(lx);

	if (lx->push != NULL) {
		if (-1 == lx->push(lx->buf_opaque, '\0')) {
			return TOK_ERROR;
		}
	}

	return t;
}

